package Fedyk3212.additionalgraphics;

import Fedyk3212.config.Configs;
import Fedyk3212.resource.Imageloader;
import org.json.simple.parser.ParseException;

import javax.imageio.ImageIO;
import javax.swing.*;
import java.awt.*;
import java.awt.image.BufferedImage;
import java.io.IOException;

public class LoadingScreen {
    static class configuredFrame{
        private JFrame frame;
        private configuredFrame(){
            frame = new JFrame();
            frame.setSize(600, 150);
            frame.setLocationRelativeTo(null);
            frame.setResizable(false);
            frame.setUndecorated(true);
            frame.getContentPane().setBackground(new Color(0xFFFFFF));
            frame.setVisible(true);
        }

        public JFrame getFrame() {
            return frame;
        }
        public void DestroyFrame(){
            frame.setVisible(false);
            frame = null;
        }
    }
    public static void Draw() throws IOException, InterruptedException, ParseException {
        String base = "";
        if (Configs.readByName("isShowed").equals("false")){
            configuredFrame frame = new configuredFrame();
            frame.getFrame().add(new JLabel(Imageloader.loadIcon(base)));
            Thread.sleep(3);
            Configs.changeByName("isShowed", "true");
            frame.DestroyFrame();
        }
    }
}
